// Link component
<a href="/dashboard" className="text-primary-500 hover:text-primary-600 underline">
  View Dashboard
</a>

// Icon with primary color
<CreditCard className="w-5 h-5 text-primary-500" />

// Focus ring
<input className="focus:ring-2 focus:ring-primary-500 focus:border-transparent" />

// Secondary button
<button className="bg-secondary-600 hover:bg-secondary-700 text-white px-6 py-3 rounded-xl">
  Save Draft
</button>

// Secondary link
<a className="text-secondary-600 hover:text-secondary-700">Learn More</a>

// Success toast
<div className="bg-success/10 border border-success/20 rounded-lg p-4">
  <p className="text-success-dark">Payment successful!</p>
</div>

// Success badge
<span className="bg-success text-white px-3 py-1 rounded-full text-xs">
  Completed
</span>

// Credit transaction
<span className="text-success font-semibold">+₹1,23,456.00</span>

// Error message
<div className="bg-error/10 border border-error/20 rounded-lg p-4">
  <p className="text-error-dark">Payment failed. Please try again.</p>
</div>

// Danger button
<button className="bg-error hover:bg-error-dark text-white px-6 py-3 rounded-xl">
  Delete Account
</button>

// Debit transaction
<span className="text-error font-semibold">-₹500.00</span>

// Transaction amount with color coding
const getAmountColor = (type: 'credit' | 'debit') => {
  return type === 'credit' ? 'text-success' : 'text-error';
};

<span className={`${getAmountColor(transaction.type)} font-semibold`}>
  {transaction.type === 'credit' ? '+' : '-'}₹{formatINR(transaction.amount)}
</span>

// Status badge
const getStatusBadge = (status: TransactionStatus) => {
  const config = {
    credit: { bg: 'bg-success/10', text: 'text-success', border: 'border-success/20' },
    pending: { bg: 'bg-warning/10', text: 'text-warning', border: 'border-warning/20' },
    failed: { bg: 'bg-error/10', text: 'text-error', border: 'border-error/20' },
  };

  const { bg, text, border } = config[status];

  return (
    <span className={`${bg} ${text} ${border} border px-3 py-1 rounded-full text-xs font-medium`}>
      {status.toUpperCase()}
    </span>
  );
};

:root {
  /* Primary Colors */
  --color-primary-50: #eff6ff;
  --color-primary-100: #dbeafe;
  --color-primary-200: #bfdbfe;
  --color-primary-300: #93c5fd;
  --color-primary-400: #60a5fa;
  --color-primary-500: #3b82f6;
  --color-primary-600: #2563eb;
  --color-primary-700: #1d4ed8;
  --color-primary-800: #1e40af;
  --color-primary-900: #1e3a8a;

  /* Secondary Colors */
  --color-secondary-50: #f0fdfa;
  --color-secondary-100: #ccfbf1;
  --color-secondary-200: #99f6e4;
  --color-secondary-300: #5eead4;
  --color-secondary-400: #2dd4bf;
  --color-secondary-500: #14b8a6;
  --color-secondary-600: #0d9488;
  --color-secondary-700: #0f766e;
  --color-secondary-800: #115e59;
  --color-secondary-900: #134e4a;

  /* Semantic Colors */
  --color-success: #22c55e;
  --color-success-light: #dcfce7;
  --color-success-dark: #15803d;
  --color-error: #ef4444;
  --color-error-light: #fee2e2;
  --color-error-dark: #b91c1c;
  --color-warning: #f59e0b;
  --color-warning-light: #fef3c7;
  --color-warning-dark: #b45309;
  --color-info: #3b82f6;
  --color-info-light: #dbeafe;
  --color-info-dark: #1d4ed8;
}

/* Usage Example */
.button-primary {
  background-color: var(--color-primary-600);
}
