const TransactionCard = ({ transaction }) => {
  const statusBadge = {
    success: (
      <span className="px-3 py-1 rounded-full text-xs font-medium bg-green-500/20 text-green-400 border border-green-400/30">
        Success
      </span>
    ),
    failed: (
      <span className="px-3 py-1 rounded-full text-xs font-medium bg-red-500/20 text-red-400 border border-red-400/30">
        Failed
      </span>
    ),
    pending: (
      <span className="px-3 py-1 rounded-full text-xs font-medium bg-yellow-500/20 text-yellow-400 border border-yellow-400/30">
        Pending
      </span>
    ),
  };

  return (
    <Card variant="default" padding="md">
      <div className="flex items-center justify-between mb-3">
        <span className="text-sm text-white/60">TXN{transaction.id}</span>
        {statusBadge[transaction.status]}
      </div>
      <div className="text-2xl font-bold text-white">
        ₹{transaction.amount.toLocaleString('en-IN')}
      </div>
    </Card>
  );
};

const KYCStatus = ({ status }) => {
  const badges = {
    verified: (
      <span className="px-3 py-1 rounded-full text-xs font-medium bg-green-500/20 text-green-400 border border-green-400/30">
        ✓ Verified
      </span>
    ),
    pending: (
      <span className="px-3 py-1 rounded-full text-xs font-medium bg-yellow-500/20 text-yellow-400 border border-yellow-400/30">
        ⏳ In Review
      </span>
    ),
    rejected: (
      <span className="px-3 py-1 rounded-full text-xs font-medium bg-red-500/20 text-red-400 border border-red-400/30">
        ✗ Rejected
      </span>
    ),
    not_started: (
      <span className="px-3 py-1 rounded-full text-xs font-medium bg-white/10 text-white/90 border border-white/20">
        Not Started
      </span>
    ),
  };

  return (
    <div className="flex items-center justify-between">
      <span className="text-sm text-white/90">KYC Status</span>
      {badges[status]}
    </div>
  );
};

const NotificationBell = ({ count }) => {
  return (
    <button className="relative p-2 rounded-lg hover:bg-white/10 transition-colors">
      <Bell className="w-6 h-6 text-white" />
      {count > 0 && (
        <span className="absolute -top-1 -right-1 w-5 h-5 rounded-full bg-red-500 text-white text-[10px] font-bold flex items-center justify-center border-2 border-white/20 animate-pulse">
          {count > 99 ? '99+' : count}
        </span>
      )}
    </button>
  );
};

const FeatureCard = ({ title, isNew, isBeta }) => {
  return (
    <Card variant="glass" padding="lg" hover={true}>
      <div className="flex items-center gap-2 mb-3">
        <h3 className="text-xl font-semibold text-white">{title}</h3>
        {isNew && (
          <span className="px-2 py-0.5 rounded text-[10px] font-bold bg-gradient-to-r from-blue-500 to-teal-500 text-white">
            NEW
          </span>
        )}
        {isBeta && (
          <span className="px-2 py-0.5 rounded text-[10px] font-bold bg-yellow-500/20 text-yellow-400 border border-yellow-400/30">
            BETA
          </span>
        )}
      </div>
      <p className="text-white/70">Feature description...</p>
    </Card>
  );
};

const SettlementStatus = ({ settlements }) => {
  const statusColors = {
    settled: 'bg-green-500/20 text-green-400 border-green-400/30',
    processing: 'bg-blue-500/20 text-blue-400 border-blue-400/30',
    pending: 'bg-yellow-500/20 text-yellow-400 border-yellow-400/30',
    failed: 'bg-red-500/20 text-red-400 border-red-400/30',
  };

  return (
    <div className="space-y-3">
      {settlements.map((settlement) => (
        <div key={settlement.id} className="flex items-center justify-between">
          <div>
            <p className="text-sm font-medium text-white">{settlement.merchant}</p>
            <p className="text-xs text-white/50">{settlement.date}</p>
          </div>
          <div className="flex items-center gap-3">
            <span className="text-lg font-bold text-white">
              ₹{settlement.amount.toLocaleString('en-IN')}
            </span>
            <span className={`px-3 py-1 rounded-full text-xs font-medium border ${statusColors[settlement.status]}`}>
              {settlement.status.charAt(0).toUpperCase() + settlement.status.slice(1)}
            </span>
          </div>
        </div>
      ))}
    </div>
  );
};

