  leftIcon={<Search className="w-5 h-5" />}
  variant="outlined"
/>

import TextField from '@/components/ui/TextField';
import { Phone } from 'lucide-react';
import { useState } from 'react';

function PhoneField() {
  const [phone, setPhone] = useState('');
  const [error, setError] = useState('');

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value.replace(/\D/g, ''); // Only digits
    setPhone(value);

    if (value.length === 10) {
      if (!/^[6-9]/.test(value)) {
        setError('Indian mobile numbers start with 6-9');
      } else {
        setError('');
      }
    } else if (value.length > 0) {
      setError('Mobile number must be 10 digits');
    } else {
      setError('');
    }
  };

  return (
    <TextField
      name="mobile"
      label="Mobile Number"
      type="tel"
      value={phone}
      onChange={handleChange}
      leftIcon={<Phone className="w-5 h-5" />}
      placeholder="9876543210"
      maxLength={10}
      error={!!error}
      errorMessage={error}
      helperText={!error ? 'Enter 10-digit mobile number' : undefined}
      required
    />
  );
}

import TextField from '@/components/ui/TextField';
import { Copy, Check } from 'lucide-react';
import { useState } from 'react';

function TransactionIdField({ transactionId }: { transactionId: string }) {
  const [copied, setCopied] = useState(false);

  const handleCopy = async () => {
    await navigator.clipboard.writeText(transactionId);
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  return (
    <TextField
      name="transactionId"
      label="Transaction ID"
      value={transactionId}
      readOnly
      rightIcon={
        <button onClick={handleCopy} className="cursor-pointer">
          {copied ? (
            <Check className="w-5 h-5 text-green-400" />
          ) : (
            <Copy className="w-5 h-5 text-white/60 hover:text-white" />
          )}
        </button>
      }
    />
  );
}

