// Utility function
export const formatCurrency = (amount: number): string => {
  return new Intl.NumberFormat('en-IN', {
    style: 'currency',
    currency: 'INR',
    minimumFractionDigits: 2,
    maximumFractionDigits: 2,
  }).format(amount);
};

// Usage
formatCurrency(125000); // ₹1,25,000.00
formatCurrency(1500000); // ₹15,00,000.00

// Large amounts in dashboard
<div className="text-3xl font-bold text-white">
  ₹{(amount / 100000).toFixed(2)}L
</div>

// Detailed amounts in tables
<div className="text-sm font-medium text-white">
  {formatCurrency(amount)}
</div>

// Color coding by transaction type
<span className={amount > 0 ? 'text-green-400' : 'text-red-400'}>
  {amount > 0 ? '+' : ''}{formatCurrency(Math.abs(amount))}
</span>

const TransactionTimeline = ({ events }) => (
  <div className="space-y-4">
    {events.map((event, index) => (
      <div key={index} className="flex items-start space-x-4">
        <div className="relative">
          <div className={`w-10 h-10 rounded-full flex items-center justify-center ${
            event.status === 'success' ? 'bg-green-500/20' :
            event.status === 'failed' ? 'bg-red-500/20' :
            'bg-blue-500/20'
          }`}>
            {event.status === 'success' && <CheckCircle className="w-5 h-5 text-green-400" />}
            {event.status === 'failed' && <XCircle className="w-5 h-5 text-red-400" />}
            {event.status === 'pending' && <Clock className="w-5 h-5 text-yellow-400" />}
          </div>
          {index < events.length - 1 && (
            <div className="absolute left-5 top-10 w-0.5 h-full bg-white/10" />
          )}
        </div>
        <div className="flex-1">
          <p className="text-sm font-medium text-white">{event.title}</p>
          <p className="text-xs text-white/60 mt-1">{event.description}</p>
          <p className="text-xs text-white/40 mt-1">
            {new Date(event.timestamp).toLocaleString('en-IN')}
          </p>
        </div>
      </div>
    ))}
  </div>
);

const SettlementCard = ({ settlement }) => {
  const getStatusColor = (status) => {
    switch (status) {
      case 'settled': return 'text-green-400 bg-green-500/20';
      case 'processing': return 'text-blue-400 bg-blue-500/20';
      case 'pending': return 'text-yellow-400 bg-yellow-500/20';
      case 'failed': return 'text-red-400 bg-red-500/20';
      default: return 'text-white/60 bg-white/10';
    }
  };

  return (
    <Card variant="default" padding="md" hover={true}>
      <div className="flex items-center justify-between mb-4">
        <div>
          <p className="text-xs text-white/60">Settlement ID</p>
          <p className="text-sm font-mono text-white">{settlement.id}</p>
        </div>
        <span className={`px-3 py-1 rounded-full text-xs font-medium ${getStatusColor(settlement.status)}`}>
          {settlement.status.toUpperCase()}
        </span>
      </div>

      <div className="space-y-2">
        <div className="flex justify-between text-sm">
          <span className="text-white/60">Amount</span>
          <span className="font-bold text-white">{formatCurrency(settlement.amount)}</span>
        </div>
        <div className="flex justify-between text-sm">
          <span className="text-white/60">Transaction Count</span>
          <span className="text-white">{settlement.txnCount}</span>
        </div>
        <div className="flex justify-between text-sm">
          <span className="text-white/60">Settlement Date</span>
          <span className="text-white">{new Date(settlement.date).toLocaleDateString('en-IN')}</span>
        </div>
      </div>

      {settlement.status === 'processing' && (
        <div className="mt-4">
          <div className="flex items-center justify-between text-xs text-white/60 mb-2">
            <span>Processing</span>
            <span>{settlement.progress}%</span>
          </div>
          <div className="w-full bg-white/10 rounded-full h-2">
            <div
              className="bg-blue-500 h-2 rounded-full transition-all duration-500"
              style={{ width: `${settlement.progress}%` }}
            />
          </div>
        </div>
      )}
    </Card>
  );
};

const KYCStatusBanner = ({ status, message }) => {
  const statusConfig = {
    not_started: {
      color: 'bg-white/10 border-white/20',
      textColor: 'text-white/70',
      icon: AlertCircle,
      iconColor: 'text-white/60',
      title: 'KYC Not Started',
      action: 'Start KYC',
      actionColor: 'primary',
    },
    pending: {
      color: 'bg-yellow-500/10 border-yellow-500/20',
      textColor: 'text-yellow-400',
      icon: Clock,
      iconColor: 'text-yellow-400',
      title: 'KYC Under Review',
      action: 'View Status',
      actionColor: 'secondary',
    },
    approved: {
      color: 'bg-green-500/10 border-green-500/20',
      textColor: 'text-green-400',
      icon: CheckCircle,
      iconColor: 'text-green-400',
      title: 'KYC Approved',
      action: null,
      actionColor: null,
    },
    rejected: {
      color: 'bg-red-500/10 border-red-500/20',
      textColor: 'text-red-400',
      icon: XCircle,
      iconColor: 'text-red-400',
      title: 'KYC Rejected',
      action: 'Resubmit',
      actionColor: 'danger',
    },
  };

  const config = statusConfig[status];
  const Icon = config.icon;

  return (
    <div className={`p-6 rounded-xl border ${config.color}`}>
      <div className="flex items-start justify-between">
        <div className="flex items-start space-x-4">
          <div className={`w-12 h-12 rounded-full flex items-center justify-center ${config.color}`}>
            <Icon className={`w-6 h-6 ${config.iconColor}`} />
          </div>
          <div>
            <h3 className={`text-lg font-semibold ${config.textColor}`}>
              {config.title}
            </h3>
            <p className="text-sm text-white/60 mt-1">{message}</p>
          </div>
        </div>
        {config.action && (
          <Button variant={config.actionColor} size="sm">
            {config.action}
          </Button>
        )}
      </div>
    </div>
  );
};

const AuditLogEntry = ({ entry }) => (
  <div className="flex items-start space-x-4 p-4 border-l-2 border-blue-400/30 bg-white/5 rounded-r-xl">
    <div className="w-10 h-10 bg-blue-500/20 rounded-full flex items-center justify-center flex-shrink-0">
      <Shield className="w-5 h-5 text-blue-400" />
    </div>
    <div className="flex-1">
      <div className="flex items-center justify-between mb-1">
        <p className="text-sm font-medium text-white">{entry.action}</p>
        <span className="text-xs text-white/40">
          {new Date(entry.timestamp).toLocaleString('en-IN')}
        </span>
      </div>
      <p className="text-xs text-white/60 mb-2">{entry.description}</p>
      <div className="flex items-center space-x-4 text-xs">
        <span className="text-white/50">User: {entry.user}</span>
        <span className="text-white/50">IP: {entry.ipAddress}</span>
      </div>
    </div>
  </div>
);

const AuditTrail = ({ transactionId }) => {
  const { data: auditLogs } = useAuditLogs(transactionId);

  return (
    <Card variant="default" padding="lg">
      <h3 className="text-lg font-semibold text-white mb-4">Audit Trail</h3>
      <div className="space-y-3">
        {auditLogs.map((entry) => (
          <AuditLogEntry key={entry.id} entry={entry} />
        ))}
      </div>
    </Card>
  );
};

