import { motion } from 'framer-motion';

export default function SnakeSpinner({ size = 64, color = '#006CED', segments = 12 }) {
  const radius = size * 0.44;

  return (
    <div className="relative" style={{ width: size, height: size }}>
      {Array.from({ length: segments }).map((_, i) => {
        const delay = (i * 0.08);
        const dotSize = size * 0.125;

        return (
          <motion.div
            key={i}
            className="absolute rounded-full"
            style={{
              width: dotSize,
              height: dotSize,
              left: '50%',
              top: '50%',
              marginLeft: -dotSize / 2,
              marginTop: -dotSize / 2,
              background: `linear-gradient(to bottom right, ${color}, ${color}DD)`,
            }}
            animate={{
              rotate: 360,
              scale: [1, 1.3, 1],
              opacity: [0.4, 1, 0.4],
            }}
            transition={{
              rotate: {
                duration: 2,
                repeat: Infinity,
                ease: "linear",
                delay: delay,
              },
              scale: {
                duration: 2,
                repeat: Infinity,
                ease: "easeInOut",
                delay: delay,
              },
              opacity: {
                duration: 2,
                repeat: Infinity,
                ease: "easeInOut",
                delay: delay,
              },
            }}
            transformTemplate={({ rotate }) =>
              `rotate(${rotate}) translateX(${radius}px)`
            }
          />
        );
      })}
    </div>
  );
}

import { motion } from 'framer-motion';

export default function BouncingDots({ color = '#006CED', dotSize = 16 }) {
  return (
    <div className="flex gap-3">
      {[0, 1, 2].map((i) => (
        <motion.div
          key={i}
          className="rounded-full"
          style={{
            width: dotSize,
            height: dotSize,
            background: `linear-gradient(to bottom right, ${color}, ${color}DD)`,
          }}
          animate={{ y: [0, -20, 0] }}
          transition={{
            duration: 0.6,
            repeat: Infinity,
            delay: i * 0.15,
            ease: "easeInOut",
          }}
        />
      ))}
    </div>
  );
}

import { motion } from 'framer-motion';

export default function FadeZoomSplash() {
  return (
    <div className="fixed inset-0 bg-gradient-to-br from-primary-600 via-primary-500 to-secondary-500 flex items-center justify-center">
      <motion.div
        initial={{ opacity: 0, scale: 0.5 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 1, ease: "easeOut" }}
        className="text-center"
      >
        <motion.img
          src="/brand/logos/Asset 2.svg"
          alt="SabPaisa"
          className="h-24 w-auto mx-auto mb-8"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.3, duration: 0.8 }}
        />
        <motion.div
          initial={{ width: 0 }}
          animate={{ width: "100%" }}
          transition={{ delay: 0.8, duration: 1.2, ease: "easeInOut" }}
          className="h-1 bg-white/30 rounded-full mx-auto max-w-xs"
        >
          <motion.div
            initial={{ width: "0%" }}
            animate={{ width: "100%" }}
            transition={{ delay: 0.8, duration: 1.5, ease: "easeInOut" }}
            className="h-full bg-white rounded-full"
          />
        </motion.div>
      </motion.div>
    </div>
  );
}

