import { useState } from 'react';
import { CreditCard, Smartphone, Building } from 'lucide-react';

interface PaymentMethod {
  id: string;
  name: string;
  icon: any;
}

const paymentMethods: PaymentMethod[] = [
  { id: 'card', name: 'Card', icon: CreditCard },
  { id: 'upi', name: 'UPI', icon: Smartphone },
  { id: 'netbanking', name: 'Net Banking', icon: Building },
];

export default function PaymentCheckout({ amount }: { amount: number }) {
  const [selected, setSelected] = useState('card');
  const [processing, setProcessing] = useState(false);

  const handlePayment = async () => {
    setProcessing(true);
    // Payment processing logic here
    await new Promise(resolve => setTimeout(resolve, 2000));
    setProcessing(false);
  };

  return (
    <div className="max-w-md mx-auto p-6 bg-white dark:bg-gray-800 rounded-xl shadow-lg">
      <h2 className="text-2xl font-bold mb-6 text-gray-900 dark:text-white">Complete Payment</h2>

      {/* Amount Display */}
      <div className="mb-6 p-4 bg-blue-50 dark:bg-blue-900/20 rounded-lg">
        <p className="text-sm text-gray-600 dark:text-gray-400">Total Amount</p>
        <p className="text-3xl font-bold text-blue-600 dark:text-blue-400">
          ₹{amount.toLocaleString('en-IN')}
        </p>
      </div>

      {/* Payment Method Selection */}
      <div className="space-y-3 mb-6">
        <p className="text-sm font-semibold text-gray-700 dark:text-gray-300">Select Payment Method</p>
        {paymentMethods.map((method) => (
          <button
            key={method.id}
            onClick={() => setSelected(method.id)}
            className={`w-full flex items-center gap-3 p-4 rounded-lg border-2 transition-all ${
              selected === method.id
                ? 'border-blue-600 bg-blue-50 dark:bg-blue-900/20'
                : 'border-gray-200 dark:border-gray-700 hover:border-blue-300'
            }`}
          >
            <method.icon className="w-6 h-6" />
            <span className="font-medium">{method.name}</span>
          </button>
        ))}
      </div>

      {/* Pay Button */}
      <button
        onClick={handlePayment}
        disabled={processing}
        className="w-full py-3 px-6 bg-blue-600 hover:bg-blue-700 text-white font-semibold rounded-lg disabled:opacity-50 transition-colors"
      >
        {processing ? 'Processing...' : `Pay ₹${amount.toLocaleString('en-IN')}`}
      </button>
    </div>
  );
}

import { useState } from 'react';
import { Check, ChevronRight, ChevronLeft } from 'lucide-react';

interface Step {
  id: number;
  title: string;
  description: string;
}

const steps: Step[] = [
  { id: 1, title: 'Personal Info', description: 'Basic details' },
  { id: 2, title: 'Business Info', description: 'Company details' },
  { id: 3, title: 'Documents', description: 'Upload files' },
  { id: 4, title: 'Review', description: 'Confirm details' },
];

export default function MultiStepForm() {
  const [currentStep, setCurrentStep] = useState(1);
  const [formData, setFormData] = useState({});

  const nextStep = () => setCurrentStep(Math.min(currentStep + 1, steps.length));
  const prevStep = () => setCurrentStep(Math.max(currentStep - 1, 1));

  return (
    <div className="max-w-3xl mx-auto p-6">
      {/* Progress Indicator */}
      <div className="mb-8">
        <div className="flex items-center justify-between">
          {steps.map((step, index) => (
            <div key={step.id} className="flex items-center flex-1">
              <div className="flex flex-col items-center">
                <div
                  className={`w-10 h-10 rounded-full flex items-center justify-center font-semibold ${
                    step.id < currentStep
                      ? 'bg-green-500 text-white'
                      : step.id === currentStep
                      ? 'bg-blue-600 text-white'
                      : 'bg-gray-200 text-gray-600'
                  }`}
                >
                  {step.id < currentStep ? <Check className="w-5 h-5" /> : step.id}
                </div>
                <p className="mt-2 text-sm font-medium">{step.title}</p>
                <p className="text-xs text-gray-500">{step.description}</p>
              </div>
              {index < steps.length - 1 && (
                <div className={`flex-1 h-1 mx-4 ${
                  step.id < currentStep ? 'bg-green-500' : 'bg-gray-200'
                }`} />
              )}
            </div>
          ))}
        </div>
      </div>

      {/* Form Content */}
      <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-6 mb-6">
        <h2 className="text-xl font-bold mb-4">{steps[currentStep - 1].title}</h2>
        {/* Step-specific content goes here */}
        <div className="min-h-[200px]">
          <p className="text-gray-600 dark:text-gray-400">Step {currentStep} content</p>
        </div>
      </div>

      {/* Navigation Buttons */}
      <div className="flex justify-between">
        <button
          onClick={prevStep}
          disabled={currentStep === 1}
          className="flex items-center gap-2 px-6 py-2 border border-gray-300 rounded-lg disabled:opacity-50 hover:bg-gray-50"
        >
          <ChevronLeft className="w-4 h-4" />
          Previous
        </button>
        <button
          onClick={nextStep}
          disabled={currentStep === steps.length}
          className="flex items-center gap-2 px-6 py-2 bg-blue-600 text-white rounded-lg disabled:opacity-50 hover:bg-blue-700"
        >
          {currentStep === steps.length ? 'Submit' : 'Next'}
          <ChevronRight className="w-4 h-4" />
        </button>
      </div>
    </div>
  );
}
